unitsize(3mm);
defaultpen(linewidth(.7pt)+fontsize(8pt));
import graph;

draw((-8,0)--(8,0),Arrows(4));
draw((0,-8)--(0,8),Arrows(4));

real f(real x) {return 2-x;}
real g(real x) {return 2x-x^2;}

real x;

draw(graph(f,-5,1),BeginArrow(4));
draw(graph(g,1,4),EndArrow(4));

real eps = 0.2;

draw((-eps,3)--(eps,3));
draw((-eps,0)--(eps,0));
draw((-eps,-3)--(eps,-3));

dot("$(-1,3)$",(-1,3),SW);
dot("$(2,0)$",(2,0),NE);
dot("$(3,-3)$",(3,-3),E);

label("$f(x)$",(1.5,8.5));
label("$x$",(8.5,-1));
[/asy]
